import React, { Component } from "react";

class Search extends Component {
  constructor(props) {
    super(props);

    this.state = {
      text: ""
    };
  }

  onSearch(e) {
    this.setState({
      text: e.target.value
    });
    // this.props.onSearch(e.target.value);
  }

  render() {
    return (
      <div id="search">
        <input type="text" onChange={e => this.onSearch.bind(e.target.value)} />
      </div>
    );
  }
}

export default Search;
